def getAmountInPrice():
  amount = 1
  # 0x6B175474E89094C44Da98b954EedeAC495271d0F
  # 0xa0b86991c6218b36c1d19d4a2e9eb0ce3606eb48
  address1 = web3.toChecksumAddress("0x6B175474E89094C44Da98b954EedeAC495271d0F") 
  address2 = web3.toChecksumAddress("0xc02aaa39b223fe8d0a0e5c4f27ead9083c756cc2")

  amountToken1 = web3.toWei(amount, 'ether') 
  factoryContract = web3.eth.contract(factory, abi=factoryABI)  
  getPair = factoryContract.functions.getPair(address1,address2).call()

  print(f"\nPair address : {getPair} ")

  pairContract = web3.eth.contract(getPair, abi= pairABi)

  reserves = pairContract.functions.getReserves().call()
  reserve0 = reserves[0]
  reserve1 = reserves[1]

  print(f"\nReserve 0 : {reserve0} Reserve 1 : {reserve1} ")

  routerContract = web3.eth.contract(router, abi=uniABI)
  price0 = routerContract.functions.getAmountIn(amountToken1,reserve0,reserve1).call()
  price1 = routerContract.functions.getAmountIn(amount,reserve1,reserve0).call()

  print(f"\nPrice 0 : {price0 / 10 ** 18} \nPrice 1 : {price1 / 10 ** 18} ")

